        public static string OsiolInformation = @"ssak z rodziny koniowatych. Charakteryzuje się dużą głową, długimi uszami, cienkim ogonem z kitą na końcu, małymi wymaganiami pokarmowymi i odpornością na brak wody. W stanie dzikim występuje osioł afrykański (Equus africanus) z podgatunkami (osioł nubijski i osioł somalijski). W wielu regionach świata użytkowana przez człowieka jest jego udomowiona forma – osioł domowy (Equus asinus). Wyselekcjonowano 78 ras, m.in.: osioł kataloński, osioł poitou, osioł asinara, osioł andaluzyjski, osioł krapass, osioł ponui, osioł prowansalski, american mammoth donkey."; public static string PiesInformation = @"(Canis lupus familiaris) – udomowiona forma wilka szarego, ssaka drapieżnego z rodziny psowatych (Canidae), uznawana przez niektórych za podgatunek wilka, a przez innych za odrębny gatunek, opisywany pod synonimicznymi nazwami Canis lupus familiaris lub Canis familiaris. Od czasu jego udomowienia powstało wiele ras, znacznie różniących się morfologią i cechami użytkowymi. Rasy pierwotne powstawały głównie w wyniku presji środowiskowej. Rasy współczesne uzyskano w wyniku doboru sztucznego. ";
        public static string KotInformation = @"(Felis catus, również Felis silvestris catus lub Felis (silvestris) domesticus) – udomowiony gatunek ssaka z rzędu drapieżnych z rodziny kotowatych. Koty zostały udomowione około 9500 lat temu i są obecnie najpopularniejszymi zwierzętami domowymi na świecie. Gatunek prawdopodobnie pochodzi od kota nubijskiego, przy czym w Europie krzyżował się ze żbikiem. Jest uznawany za gatunek inwazyjny. ";
        public static string KonInformation = @"(Equus caballus) – gatunek ssaka nieparzystokopytnego z rodziny koniowatych. Koń po raz pierwszy został udomowiony prawdopodobnie na terenie północnego Kazachstanu w okresie kultury Botai tj. około 3,5 tys. lat p.n.e., natomiast ludność europejska dokonała tego ok. 1,5 tys. lat p.n.e. Obecnie przedstawiciele 18 z 21 współczesnych ras hodowlanych pochodzą od dwóch linii – arabskiej i turkmeńskiej. ";
        public static string SwiniaInformation = @"(Sus scrofa f. domestica) – zwierzę hodowlane, udomowione między VII a VI tys. lat p.n.e. Dostarcza hodowcom mięsa, tłuszczu, skóry, podrobów, szczeciny. Jest ssakiem z rzędu parzystokopytnych, rodziny świniowatych. ";
        public static string PapugaInformation = @" (Psittaciformes) – rząd ptaków z podgromady Neornithes. Najliczniejsze w Ameryce Południowej i Australii. ";
        public static string KrabInformation = @"Kraby (Brachyura, z języka greckiego krótki odwłok/ogon) – grupa krótkoodwłokowych skorupiaków z rzędu dziesięcionogów (Decapoda). Kraby są najliczniejszą (opisano 10 500 gatunków) grupą dziesięcionogów, u której pierwsza para odnóży krocznych przekształciła się w szczypce. Wielkość ciała bardzo zróżnicowana, o rozpiętości odnóży od kilku milimetrów (Pinnotheres pisum) do prawie 4 m (japoński krab pacyficzny). ";
        public static string NiedzwiedzInformation = @"duży ssak drapieżny o masywnej budowie ciała, krótkich kończynach, szczątkowym ogonie, pokryty długim, gęstym futrem";
        public static string ZyrafaInformation = @"Żyrafa (Giraffa camelopardalis) – afrykański ssak parzystokopytny z rodziny żyrafowatych, najwyższe zwierzę lądowe i największy z przeżuwaczy żyjących w czasach nowożytnych. Jego epitet gatunkowy odnosi się do przypominającego wielbłąda wyglądu i łat na futrze, właściwych lampartowi (leopardowi, Panthera pardus). Gatunek ten wyróżnia się bardzo długą szyją, osiąga 5–6 m wysokości i masę (średnio) 1200 kg w przypadku samców i 830 kg w przypadku samic. Jego najbliższym żyjącym krewnym jest okapi leśne (rodzina Giraffidae liczy na początku XXI w. tylko te dwa gatunki). Bazując na wzorze ubarwienia wyróżnia się 9 podgatunków, natomiast badania genetyczne sugerują podział gatunku na cztery odrębne gatunki. ";
    }
